    qty: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        row.amount = (row.qty || 0) * (row.rent_rate || 0);
        frm.refresh_field("rental_items");
    },
    rent_rate: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        row.amount = (row.qty || 0) * (row.rent_rate || 0);
        frm.refresh_field("rental_items");
    }
});
